                <Button
                  onClick={() => createDidMutation.mutate()}
                  disabled={createDidMutation.isPending}
                  className="flex-1"
                  data-testid="button-confirm-create"
                >
                  {createDidMutation.isPending ? "Creating..." : "Create DID"}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
